G4Material *MyMaterials::Vacuum()
{
  G4double a, z, density;
  G4int nelements;

  G4Element *N = new G4Element("Nitrogen", "N", z = 7, a = 14.01 * g / mole);
  G4Element *O = new G4Element("Oxygen", "O", z = 8, a = 16.00 * g / mole);

  G4Material *mat = new G4Material("Vacuum", density = 0.001 * mg / cm3, nelements = 2);
  mat->AddElement(N, 70. * perCent);
  mat->AddElement(O, 30. * perCent);

  const G4int nEntries = 3;
  G4double PhotonEnergy[nEntries] = {0.0001 * eV, 1.00 * eV, 100.00 * eV};
  G4double RefractiveIndex[nEntries] = {1.00, 1.00, 1.00};

  G4MaterialPropertiesTable *myMPT = new G4MaterialPropertiesTable();
  myMPT->AddProperty("RINDEX", PhotonEnergy, RefractiveIndex, nEntries);

  mat->SetMaterialPropertiesTable(myMPT);

  return mat;
}

G4Material *MyMaterials::Silicon()
{
  G4double a, z, density;

  G4Element *Si = new G4Element("Silicon", "Si", z = 14., a = 28.09 * g / mole);

  G4Material *mat = new G4Material("Silicon", density = 2.33 * g / cm3, 1);
  mat->AddElement(Si, 1);

  return mat;
}
G4Material *MyMaterials::Bialkali()
{
  static G4Element *K = G4NistManager::Instance()->FindOrBuildElement("K");
  static G4Element *Sb = G4NistManager::Instance()->FindOrBuildElement("Sb");
  static G4Element *Cs = G4NistManager::Instance()->FindOrBuildElement("Cs");

  G4Material *material = new G4Material("Bialkali", 4.28 * g / cm3, 3, kStateSolid);
  material->AddElement(K, 13.3 * perCent);
  material->AddElement(Cs, 45.2 * perCent);
  material->AddElement(Sb, 41.5 * perCent);
  return material;
}

G4Material *MyMaterials::PyrexGlass()
{
  const G4int flatentries = 2;
  static double photonE[flatentries] = {0.1 * eV, 6 * eV};
  static double refrac_idx[flatentries] = {1.525, 1.525};
  static double abs_length[flatentries] = {10 * m, 10 * m};

  G4MaterialPropertiesTable *table = new G4MaterialPropertiesTable();
  table->AddProperty("RINDEX", photonE, refrac_idx, flatentries);
  table->AddProperty("ABSLENGTH", photonE, abs_length, flatentries);

  G4Material *material = G4NistManager::Instance()->FindOrBuildMaterial("G4_Pyrex_Glass");
  material->SetMaterialPropertiesTable(table);
  return material;
}
G4Material *MyMaterials::silicone()
{
  G4double a, z;
  G4Element *Si = new G4Element("Silicon", "Si", z = 14., a = 28.09 * g / mole);
  G4Element *O = new G4Element("Oxygen", "O", z = 8., a = 16.00 * g / mole);
  G4Element *H = new G4Element("Hydrogen", "H", z = 1., a = 1.01 * g / mole);
  G4Element *C = new G4Element("Carbon", "C", z = 6., a = 12.01 * g / mole);

  G4Material *material = new G4Material("Silicone", 0.965 * g / cm3, 4);
  material->AddElement(C, 2);
  material->AddElement(H, 6);
  material->AddElement(O, 1);
  material->AddElement(Si, 1);

  const G4int nEntries_RI = 2;
  G4double PhotonEnergy_RI[nEntries_RI] = {1 * eV, 6.00 * eV};
  G4double RefractiveIndex[nEntries_RI] = {1.57, 1.57};
  G4MaterialPropertiesTable *myMPT = new G4MaterialPropertiesTable();
  myMPT->AddProperty("RINDEX", PhotonEnergy_RI, RefractiveIndex, nEntries_RI);
  material->SetMaterialPropertiesTable(myMPT);

  return material;
}

G4Material *MyMaterials::PlasticO2WLS() //O2 - orange green-to-wls
{
  G4double a, z, density;

  G4Element *H = new G4Element("Hydrogen", "H", z = 1., a = 1.01 * g / mole);
  G4Element *C = new G4Element("Carbon", "C", z = 6., a = 12.01 * g / mole);

  G4Material *mat = new G4Material("PlasticO2WLS", density = 1. * g / cm3, 2);
  mat->AddElement(H, 4);
  mat->AddElement(C, 2);

  const G4int nEntries_RI = 5;
  G4double PhotonEnergy_RI[nEntries_RI] = {1.0 * eV, 2.0 * eV, 2.5 * eV, 3.0 * eV, 3.5 * eV};
  G4double RefractiveIndex[nEntries_RI] = {1.5, 1.5, 1.5, 1.5, 1.5};

  const G4int nEntries_ABS = 4;
  G4double PhotonEnergy_ABS[nEntries_ABS] = {1.0 * eV, 1.84 * eV, 4.08 * eV, 6.26 * eV};
  G4double Absorption[nEntries_ABS] = {1000. * mm, 1000. * mm, 1000. * mm, 1000. * mm};

  G4MaterialPropertiesTable *myMPT = new G4MaterialPropertiesTable();
  myMPT->AddProperty("RINDEX", PhotonEnergy_RI, RefractiveIndex, nEntries_RI);
  myMPT->AddProperty("ABSLENGTH", PhotonEnergy_ABS, Absorption, nEntries_ABS);

  mat->SetMaterialPropertiesTable(myMPT);

  return mat;
}

